INSERT INTO llx_website_page(rowid, fk_page, fk_website, pageurl, aliasalt, title, description, lang, image, keywords, status, date_creation, tms, import_key, grabbed_from, type_container, htmlheader, content, author_alias, allowed_in_frames) VALUES(18__+MAX_llx_website_page__, null, __WEBSITE_ID__, '_library_lib', '', 'lib', '', '', '', '', '1', '2025-04-11 10:57:25', '2025-05-16 14:26:30', null, '', 'library', '', '<?php__N__/**__N__ * Set session language based on:__N__ * - Cookie \'lang\' (if exists)__N__ * - Browser language (if not present among website languages, use website main language)__N__ * - Form field \'language\' (if present)__N__ *__N__ * The function also updates the product labels of the products in the session__N__ * to match the current language.__N__ *__N__ * @return bool__N__ */__N__function setSessionLang() {__N__   global $langs, $website, $weblangs, $db;__N__    // Get available langs for the website__N__    $website_langs = getAvailableLangs();__N__    $available_langs = $website_langs[\'langs\'];__N__    $langs_code_shortcode = $website_langs[\'langs_code_shortcode\'];__N____N__    // If no language is set or if a new language is selected (via form field)__N__    if (!isset($_COOKIE[\'lang\']) || GETPOST(\'l\', \'alpha\')) {__N__        // Get browser language__N__        $browser_lang = $weblangs->getDefaultLang(1);__N____N__        // Change used lang__N__        $new_lang = GETPOST(\'l\', \'alpha\');__N__        if ($new_lang && array_key_exists ($new_lang, $available_langs)) {__N__            setcookie(\'lang\', $new_lang, time() + (86400 * 30), \'/\');__N__            $session_lang = $new_lang;__N__        }__N____N__        // If no language is set in cookie and browser language is not present among available languages__N__        // use website main language as default__N__        if (!isset($_COOKIE[\'lang\'])) {__N__            if (array_key_exists ($browser_lang, $available_langs)){__N__                // The browser language is