corresponding shortcodes.__N__ *__N__ * @return array__N__ */__N__function getAvailableLangs() {__N__    global $website, $weblangs;__N____N__    $available_langs = array();__N__    $langs_code_shortcode = array();__N__    $ws_langs_list = explode(\',\', $website->lang.\',\'.$website->otherlang);__N__    $dol_available_langs = $weblangs->get_available_languages(DOL_DOCUMENT_ROOT, 1, 2, 1);__N__    foreach ($ws_langs_list as $lang) {__N__        foreach ($dol_available_langs as $key => $value) {__N__            if (strpos($key, $lang) === 0) {__N__                $available_langs[$lang] = $value;__N__                $langs_code_shortcode[$lang] = $key;__N__                break;__N__            }__N__        }__N__    }__N__    __N__    return array(\'langs\' => $available_langs, \'langs_code_shortcode\' => $langs_code_shortcode);__N__}__N____N__/**__N__ * Get societe information from Dolibarr configuration.__N__ *__N__ * This function retrieves the societe information from the global $conf object__N__ * and returns it as an associative array.__N__ *__N__ * @return array__N__ */__N__function getSocInfo() {__N__    global $conf, $mysoc, $weblangs, $website;__N__    $weblangs->load(\"admin\");__N____N__    $societe_info = array(__N__        \'name\' => !empty($mysoc->name) ? html_entity_decode($mysoc->name) : \'\',__N__        \'address\' => !empty($mysoc->getFullAddress(1)) ? html_entity_decode($mysoc->getFullAddress(1)) : \'\',__N__        \'zip\' => !empty($mysoc->zip) ? html_entity_decode($mysoc->zip) : \'\',__N__        \'town\' => !empty($mysoc->town) ? html_entity_decode($mysoc->town) : \'\',__N__        \'country\' => !empty($mysoc->country) ? html_entity_decode($mysoc->country) : \'\',__N__        \'phone\' => !empty($mysoc->phone) ? html_entity_decode($mysoc->phone) : \'\',__N__        \'fax\' => !empty($mysoc->fax) ? html_entity_decode($mysoc->fax) : \'\',__N__        \'email\' => !empty($mysoc->email) ? html_entity_decode($mysoc->email) : \'\',__N__        \'slogan\' => getDolGlobalString(\'WS_\'.strtoupper($website->ref).\'_\' . \'CUSTOM_SLOGAN\') __N__                ? html_entity_decode(getDolGlobalString(\'WS_\'.strtoupper($website->ref).\'_\' . \'CUSTOM_SLOGAN\')) __N__                : (!empty($mysoc->name) __N__                    ? html_entity_decode($mysoc->name) __N__                    : html_entity_decode($weblangs->trans(\"noCompanyInfoSlogan\"))),__N__        \'description\' => getDolGlobalString(\'WS_\'.strtoupper($website->ref).\'_\' . \'CUSTOM_DESCRIPTION\') __N__                ? html_entity_decode(getDolGlobalString(\'WS_\'.strtoupper($website->ref).\'_\' . \'CUSTOM_DESCRIPTION\')) __N__                : (!empty($mysoc->socialobject) __N__                    ? html_entity_decode($mysoc->socialobject) __N__                    : html_entity_decode($weblangs->trans(\"noCompanyInfoDescription\", $weblangs->trans(\"CompanyObject\"))))__N__    );__N____N__    // Add social networks if they are set__N__    foreach($mysoc->socialnetworks as $key => $value) {__N__        if (!empty($value)) {__N__            $societe_info[$key] = preg_match(\'/^http/\', $value) ? $value : \'https://www.\'.$key.\'.com/\'.$value;__N__        }__N__    }__N__    __N__    // Add link to subscription__N__    if (isModEnabled(\'multicompany\')) {__N__      $entity_qr = \'?entity=\' . ((int) $conf->entity);__N__    } else {__N__      $entity_qr = \'\';__N__    }__N__    $urlwithouturlroot = preg_replace(\'/\' . preg_quote(DOL_URL_ROOT, \'/\') . \'$/i\', \'\', trim($conf->file->dol_main_url_root));__N__    $urlwithroot = $urlwithouturlroot . DOL_URL_ROOT;__N__    $societe_info[\'subscription_link\'] = $urlwithroot . \'/public/members/new.php\'.$entity_qr;__N__    __N__    // Add participation link__N__    $link_participation = $urlwithroot.\'/public/eventorganization/attendee_new.php?type=global\';__N__	$societe_info[\'link_participation\'] = $link_participation;__N__	__N__	// Add donation link__N__    $link_donation = $urlwithroot.\'/public/donations/new.php\';__N__	$societe_info[\'link_donation\'] = $link_donation;__N____N__    return $societe_info;__N__}', '', 0);


